"use strict";class Alert{constructor(e,t=!0){try{if(void 0===e)throw"The id is undefined.";if("string"!=typeof e)throw"The id must be type of string";this.arrStatus=["open","close"],this.arrType=["success","error"],this.createEl(),this.el.id=e,!0===t&&this.createStyle(),this.children={close:this.el.querySelector("[data-alert-close]"),msg:this.el.querySelector("[data-alert-msg]")},this.addEvents()}catch(e){console.log(e)}}createEl(){this.el=document.createElement("div"),this.el.innerHTML='<div data-alert-close>Close</div><div data-alert-msg data-alert-type=""></div>',document.querySelector("body").append(this.el)}createStyle(){this.setStatus("close");const e=document.createElement("style");e.innerHTML=`	
			#`+this.el.id+`	{	box-sizing: border-box;	background-color: whitesmoke;	padding: 10px; position: absolute;	top: 0;	left: 0; width: 100%;	}
			#`+this.el.id+`[data-alert-status='close'] { display: none; }
			#`+this.el.id+`[data-alert-status='open'] { display: flex; flex-direction: row-reverse; }
			#`+this.el.id+` > div[data-alert-close] { cursor: pointer; padding: 0 5px; border-left: 1px solid gray; }
			#`+this.el.id+` > div[data-alert-msg] { width: 100%; text-align: center; padding: 0 10px; }
			#`+this.el.id+` > div[data-alert-type="success"] { color: green; }
			#`+this.el.id+` > div[data-alert-type="error"] { color: red; }
		`,document.querySelector("head").append(e)}addEvents(){this.children.close.addEventListener("click",()=>this.setStatus("close"))}setStatus(e){if(!this.arrStatus.includes(e))return console.log("Status not valid."),!1;this.el.dataset.alertStatus=e}setType(e){if(!this.arrType.includes(e))return console.log("Type not valid."),!1;this.children.msg.dataset.alertType=e}display(e,t="success"){if(this.children.msg.innerHTML=e,!1===this.setType(t))return!1;this.setStatus("open")}}export{Alert};